import { useEffect } from "react";
import { fetchPosts} from "../api/api";

const BASE_URL = "https://strangers-things.herokuapp.com/api/2207-FTB-ET-WEB-PT";

const Home = ({posts, setPosts}) => {
    useEffect(() => {fetchPosts().then((result) => {
        setPosts(result.data.posts)
    })}, []);
    return (<div>
       {posts ? <div>{posts.map((post, index) => <div key={index}><h2>{post.title}</h2><p>{post.description}</p><p>Price: {post.price}</p></div>)}</div> : null}
        </div>
    )
}

export default Home;
